
def longestPalindrome(s: str) -> int:
    charCount = {}
    for char in s:
        if char not in charCount:
            charCount[char] =0
        charCount[char] += 1
        
    longest = 0
    longestOdd = 0
    print(charCount)
    for key, value in charCount.items():
        if value % 2 == 0:
            longest += value
        else:
            longestOdd = max(value, longestOdd)
    
    return longest+longestOdd

